package ibc_test

import (
	"encoding/hex"
	"encoding/json"
	"fmt"
	"testing"
)

var jsonEvents = ""

func hexToByte(h string) []byte {
	b, err := hex.DecodeString(h)
	if err != nil {
		fmt.Printf("%v \n", err)
		return nil
	}
	return b
}

type Result []interface{}

func TestIBCEvents(t *testing.T) {
	var result Result
	j := hexToByte(jsonEvents)
	err := json.Unmarshal(j, &result)
	if err != nil {
		t.Error(err)
	}

	fmt.Printf("### Decoded value %v \n", result)
	fmt.Printf("### Type of first value %T \n", result[0])
}
